#include <bits/stdc++.h>
using namespace std;
class Solution {
public:
    int maxFreq(string s, int minSize, int maxLetters, int maxSize) {

       set<int> stt;
        int maxa=0;
        string str="";
        map<string, int> m;
        for(int i=0;i+minSize-1<s.length();i++)
        {
            str = s.substr(i, minSize);
            for(int j=0;j<str.length();j++)
                stt.insert(str[j]-'a');

            if(stt.size()<=maxLetters)
            {
                m[str]++;
                if(m[str]>maxa)
                    maxa=m[str];
            }
             stt.clear();
        }

        return maxa;
    }
};
main(){
   Solution ob;
   cout << (ob.maxFreq("",5,12,26)) << endl;
    Solution s;
    Solution v;
    Solution c;
    cout << s.maxFreq("abcde", 2,3,3) << endl;
    cout << v.maxFreq("abcde", 2,4,26) << endl;
    cout << c.maxFreq("ababab", 2,3,4) << endl;
}
